                    url = url.replace(options.stripPrefix, "");
                }
                // Add the prefix
                if (options.prefix) {
                    url = options.prefix + url;
                }

                // Rename the url
                if (_.isFunction(options.rename)) {
                    url = options.rename(url, templateFile);
                }
            }

            return url;
        }
    }

