        return templateContent
            .replace(/\\/g, "\\\\")
            .replace(/'/g, "\\'")
            .replace(/\r?\n/g, "\\n");
    }

    function getPrettyEscapedContent(templateContent) {
        return templateContent
            .replace(/\\/g, "\\\\")
            .replace(/'/g, "\\'")
            .replace(/\r?\n/g, "\\n' +\n    '");
    }

    return map(ngHtml2Js);
};
